import React, { useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { Navigate } from 'react-router-dom';
import PageOne from './pages/PageOne.jsx';
import PageTwo from './pages/PageTwo.jsx';
import './App.css';

const token = process.env.REACT_APP_FOUNDRY_TOKEN;
function App() {
  const [submitted, setSubmitted] = useState(false);
  const [recommendationData, setRecommendationData] = useState(null);

  const handleSubmit = async (formData) => {
    setSubmitted(true);
    try {
      const response = await fetch("https://josephpongonthara.usw-16.palantirfoundry.com/api/v1/ontologies/UserProfile", {
        method: "POST",
        headers: {
          "Authorization": `Bearer ${token}`,
          "Content-Type": "application/json"
        },
        body: JSON.stringify(formData)
      });

      /*
      const dummyResponse = {
        products: ['Racket A', 'String B'],
        reasoning: 'Based on your preferences, these choices provide optimal power and control.',
      };
      setTimeout(() => setRecommendationData(dummyResponse), 2000);
      */
    } catch (error) {
      console.error("Error during fetch:", error);
    }
  };

  const handleReset = () => {
    setSubmitted(false);
    setRecommendationData(null);
  };

  return (
    <BrowserRouter>
      <Routes>
      <Route path="/" element={<Navigate to="/PageOne" replace />} />
        <Route
          path="/PageOne"
          element={<PageOne onSubmit={handleSubmit} token={token} />}
        />
        <Route
          path="/PageTwo"
          element={<PageTwo data={recommendationData} onReset={handleReset} token={token} />}
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
