import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../App.css';

const PageTwo = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const formState = location.state?.formState;

  const [userId, setUserId] = useState(null);
  const [recommendationData, setRecommendationData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [matchError, setMatchError] = useState(null);


  const handleReset = () => {
    navigate('/');
  };

  useEffect(() => {
    const fetchUserProfilesAndMatch = async () => {
      try {
        // 1. Fetch all user profiles
        const userRes = await fetch('http://localhost:5001/api/user-profiles');
        if (!userRes.ok) {
          const errorText = await userRes.text();
          throw new Error(`UserProfile to fetch: ${userRes.status} - ${errorText}`);
        }

        const userData = await userRes.json();
        const users = userData.data;

        console.log('Fetched UserProfiles:', users);

        //match formstate to a user profile
        const matchedUser = users.find(user => {
          const brandMatch = Array.isArray(user.preferredBrand)
          ? user.preferredBrand.includes(formState.brands)
          : user.preferredBrand === formState.brands;

          const aspectMatch = Array.isArray(user.aspectsToOptimize)
            ? user.aspectsToOptimize.includes(formState.optimize)
            : user.aspectsToOptimize === formState.optimize;

          return (
            user.equipmentType === formState.equipment &&
            brandMatch &&
            aspectMatch &&
            user.skillLevel === formState.playStyle &&
            String(user.playFrequency) === String(formState.frequency)
          );
        });

        if (!matchedUser){
          setMatchError('No matching user profile found.');
          setLoading(false);
          return;
        }
        const userId = matchedUser.__primaryKey || matchedUser.id;
        setUserId(userId);
        console.log('Matched user:', matchedUser);

        // Fetching all recommendation results
        const recRes = await fetch('http://localhost:5001/api/results');
        if (!recRes.ok) {
          const errorText = await recRes.text();
          throw new Error(`RecommendationResult fetch failed: ${recRes.status} - ${errorText}`);
        }

        const recData = await recRes.json();
        const results = recData.data;

        const matchedResult = results.find(result => {
          return result.linked_user === userId;
        });

        if (!matchedResult) {
          setMatchError('No matching recommendation found.');
          setLoading(false);
          return;
        }
        //storing matched recommendation
        setRecommendationData({
          racketId: matchedResult.racket_id,
          stringId: matchedResult.string_id,
          reasoning: matchedResult.llm_explanation,
        });
      } catch (err) {
        console.error('Error fetching or matching data:', err);
        setMatchError(err.message);
      } finally {
        setLoading(false);
      }
    };
    if (formState) {
      fetchUserProfilesAndMatch();
    } else {
      setMatchError('No form data available.');
      setLoading(false);
    }
  }, [formState]);

// LOADING & CHECKING FOR ERRORS   
  if (loading) {
    return <div className="loader">Loading user profile...</div>;
  }
  if (matchError) {
    return <div className="error">Error: {matchError}</div>;
  }
  //MAIN RENDER
  return (
    <div className="page">
      <h2 className="main-header">Your Personalized Recommendations</h2>
      <div className="recommendation">
        <p>{recommendationData.reasoning}</p>
        <ul>
          <li><strong>Racket ID:</strong> {recommendationData.racketId}</li>
          <li><strong>String ID:</strong> {recommendationData.stringId}</li>
          <li><strong>Reasoning:</strong> {recommendationData.reasoning}</li>
        </ul>
      </div>
      <button className="submit-button" onClick={handleReset}>
        Start Over
      </button>
    </div>
  );
};

export default PageTwo;
