import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import "../App.css";

const PageTwo = ({ token, onReset }) => {
  const location = useLocation();
  const { userInput } = location.state; // Get input passed from PageOne
  const [loading, setLoading] = useState(true);
  const [result, setResult] = useState(null);

  useEffect(() => {
    const fetchRecommendation = async () => {
      try {
        const response = await fetch(
          "https://josephpongonthara.usw-16.palantirfoundry.com/api/v1/code-repos/your-proshop-backend/functions/recommend", // replace with actual endpoint
          {
            method: "POST",
            headers: {
              "Authorization": `Bearer ${token}`,
              "Content-Type": "application/json"
            },
            body: JSON.stringify(userInput)
          }
        );

        if (!response.ok) throw new Error("Backend call failed");

        const data = await response.json();
        setResult(data);
      } catch (error) {
        console.error("Error fetching recommendation:", error);
        alert("Failed to fetch recommendation. See console for details.");
      } finally {
        setLoading(false);
      }
    };

    fetchRecommendation();
  }, [userInput, token]);

  if (loading) return <div className="loader">Loading...</div>;
  if (!result) return <div>Error fetching recommendations.</div>;

  return (
    <div className="page">
      <div className="logo-title">
        <div className="logo-placeholder" />
        <h1 className="title">Personal Pro Shop</h1>
      </div>

      <h2 className="main-header">Your Personalized Recommendations</h2>
      <div className="recommendation">
        {result.recommendations.map((rec, idx) => (
          <div key={idx} className="product">
            {rec.logo && <img src={rec.logo} alt={rec.brand} className="brand-logo" />}
            <p>{rec.name}</p>
          </div>
        ))}
      </div>

      <h3>Reasoning</h3>
      <p>{result.reasoning}</p>

      <button className="submit-button" onClick={onReset}>Generate New Recommendation</button>
    </div>
  );
};

export default PageTwo;
